package in.orangecounty.sajin;/** * Created by sajin on 08/02/17. */public class Student {    int id;    String name;    int age;    Student(int i, String n, int a) {        id = i;        name = n;        age = a;    }    void display() {        System.out.println(id + " " + name + " " + age);    }    public static void main(String args[]) {        Student s1 = new Student(01, "SAJIN", 22);        Student s2 = new Student(02, "MON", 20);        s1.display();        s2.display();    }}